#!/usr/bin/env bash# 关闭占用端口run_port=10130jar_name="ui-examination.jar"sudo fuser -k -n tcp ${run_port}# 删除历史的images和containerecho "删除docker相关容器和镜像"docker stop ui-examination-containerdocker rm ui-examination-containerdocker rmi ui-examination# 移动文件到目的位置echo "展示路径位置并处理jar包"pwdrm -f ${jar_name}mv ./target/${jar_name} ${jar_name}# 构建echo "构建和启动docker"docker build -t ui-examination .docker run -d -p ${run_port}:${run_port} --name ui-examination-container ui-examination /bin/bash